package io.choerodon.manager.api.dto;import io.choerodon.manager.api.validator.Check;import io.choerodon.manager.api.validator.Insert;import io.choerodon.manager.api.validator.Update;import io.choerodon.mybatis.entity.BaseDTO;import io.swagger.annotations.ApiModelProperty;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;import javax.validation.constraints.NotEmpty;import javax.validation.constraints.NotNull;import javax.validation.constraints.Pattern;import static io.choerodon.manager.infra.utils.RegularExpression.ALPHANUMERIC_AND_SPACE_SYMBOLS;import static io.choerodon.manager.infra.utils.RegularExpression.ROUTE_RULE_ALL_SYMBOLS_200;/** * FD_ROUTE_RULE DTO * * @author pengyuhua * @date 2019/10/25 */@Table(name = "fd_route_rule")public class RouteRuleDTO extends BaseDTO {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @ApiModelProperty(value = "主键ID")    @NotNull(message = "error.route.rule.update.id.can.not.be.null", groups = {Update.class})    private Long id;    @ApiModelProperty(value = "路由编码/必填")    @NotEmpty(message = "error.route.rule.code.can.not.be.empty", groups = {Insert.class, Check.class})    @Pattern(regexp = ALPHANUMERIC_AND_SPACE_SYMBOLS,message = "error.route.rule.code.format.incorrect", groups = {Insert.class, Check.class})    private String code;    @ApiModelProperty(value = "路由描述/选填")    @Pattern(regexp = ROUTE_RULE_ALL_SYMBOLS_200, message = "error.route.rule.description.format.incorrect", groups = {Insert.class, Update.class})    private String description;    public Long getId() {        return id;    }    public RouteRuleDTO setId(Long id) {        this.id = id;        return this;    }    public String getCode() {        return code;    }    public RouteRuleDTO setCode(String code) {        this.code = code;        return this;    }    public String getDescription() {        return description;    }    public RouteRuleDTO setDescription(String description) {        this.description = description;        return this;    }}